import React, { Component } from "react";
import "./App.css";
import 'react-dropdown/style.css';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';
import client from './client';

class Distributors extends Component {

    constructor(props) {
        super(props)
        this.state = {distributors: []};
    }

    componentDidMount() {
        client({method: 'GET', path: '/api/distributors'}).done(response => {
            this.setState({distributors: response.entity._embedded.employees});
        });
    }

    render() {
         const {distributor, isLoading} = this.state;

        if (isLoading) {
            return <p>Loading...</p>;
        }

        const distributorList = distributor.map(distributor => {
            distributor.companyName = '';
            distributor.nationalCode = '';
            distributor.distributedProductName = '';
            distributor.distributedProductCode = '';
            distributor.distributorName = '';
            distributor.phoneNumber = '';
            return <tr key={distributor.id}>
                <td style={{whiteSpace: 'nowrap'}}>{distributor.distributorName}</td>
                <td style={{whiteSpace: 'nowrap'}}>{distributor.distributedProductCode}</td>
                <td style={{whiteSpace: 'nowrap'}}>{distributor.distributedProductName}</td>
                <td style={{whiteSpace: 'nowrap'}}>{distributor.address}</td>
                <td style={{whiteSpace: 'nowrap'}}>{distributor.phoneNumber}</td>
                <td style={{whiteSpace: 'nowrap'}}>{distributor.nationalCode}</td>
                <td style={{whiteSpace: 'nowrap'}}>{distributor.companyName}</td>
                <td>
                    <ButtonGroup>
                        <Button size="sm" color="primary" tag={Link} to={"/distributor/" + distributor.id}>Edit</Button>
                        <Button size="sm" color="danger" onClick={() => this.remove(distributor.id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
        });


        return (
            <div>
                <Container fluid>
                    <div className="float-right">
                        <Button color="success" tag={Link} to="/AddDistributors">Add Distributor</Button>
                    </div>
                    <h3>Distributors</h3>
                    <Table className="mt-4">
                        <thead>
                        <tr>
                            <th width="10%">distributorNam}</th>
                            <th width="10%">distributedProductCode</th>
                            <th width="10%">distributedProductName</th>
                            <th width="10%">address</th>
                            <th width="10%">phoneNumber</th>
                            <th width="10%">nationalCode</th>
                            <th width="10%">companyName</th>
                            <th width="10%">Actions</th>
                        </tr>
                        </thead>
                        <tbody>
                        {distributorList}
                        </tbody>
                    </Table>
                </Container>
            </div>
        );
    }
}

export default Distributors;
